import React, { useRef, useState, useEffect } from 'react';
import { Camera, Upload, X } from 'lucide-react';

const ScanScreen = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);
  const [analyzing, setAnalyzing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [capturedImage, setCapturedImage] = useState(null);

  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, [stream]);

  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment', width: { ideal: 1920 }, height: { ideal: 1080 } }
      });
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
      setStream(mediaStream);
      setCameraActive(true);
    } catch (error) {
      console.error('Error accessing camera:', error);
      alert('Unable to access camera. Please check permissions.');
    }
  };

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setCameraActive(false);
  };

  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    
    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0);
    
    const imageData = canvas.toDataURL('image/jpeg');
    setCapturedImage(imageData);
    stopCamera();
    analyzeImage();
  };

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      setCapturedImage(e.target.result);
      analyzeImage();
    };
    reader.readAsDataURL(file);
  };

  const analyzeImage = () => {
    setAnalyzing(true);
    setProgress(0);

    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(() => {
            alert('Analysis complete! In a full app, this would navigate to results.');
            resetScan();
          }, 500);
          return 100;
        }
        return prev + 5;
      });
    }, 100);
  };

  const resetScan = () => {
    setCapturedImage(null);
    setAnalyzing(false);
    setProgress(0);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e8f5e9 0%, #c8e6c9 100%)',
      display: 'flex',
      flexDirection: 'column'
    }}>
      {/* Header */}
      <div style={{
        padding: '16px 20px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(10px)',
        borderBottom: '1px solid rgba(0, 0, 0, 0.05)'
      }}>
        <button
          onClick={cameraActive ? stopCamera : () => window.history.back()}
          style={{
            background: 'none',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            padding: '8px',
            color: '#2d5016'
          }}
        >
          {cameraActive ? '√ó' : '‚Üê'}
        </button>
        <h1 style={{
          fontSize: '18px',
          fontWeight: '600',
          color: '#0f2419',
          margin: 0
        }}>
          Recognition
        </h1>
        <div style={{ width: '40px' }}></div>
      </div>

      {/* Main Content */}
      <div style={{
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        padding: '20px',
        gap: '20px'
      }}>
        {/* Camera/Preview Area */}
        <div style={{
          position: 'relative',
          flex: 1,
          background: capturedImage || cameraActive ? '#000' : 'rgba(255, 255, 255, 0.7)',
          borderRadius: '24px',
          overflow: 'hidden',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '400px',
          boxShadow: '0 8px 32px rgba(45, 80, 22, 0.15)'
        }}>
          {/* Camera Frame Overlay */}
          {(cameraActive || capturedImage) && (
            <div style={{
              position: 'absolute',
              inset: '40px',
              border: '3px solid rgba(255, 255, 255, 0.5)',
              borderRadius: '20px',
              zIndex: 10,
              pointerEvents: 'none'
            }}>
              {/* Corner decorations */}
              <div style={{ position: 'absolute', top: -3, left: -3, width: '40px', height: '40px', borderTop: '6px solid white', borderLeft: '6px solid white', borderRadius: '20px 0 0 0' }}></div>
              <div style={{ position: 'absolute', top: -3, right: -3, width: '40px', height: '40px', borderTop: '6px solid white', borderRight: '6px solid white', borderRadius: '0 20px 0 0' }}></div>
              <div style={{ position: 'absolute', bottom: -3, left: -3, width: '40px', height: '40px', borderBottom: '6px solid white', borderLeft: '6px solid white', borderRadius: '0 0 0 20px' }}></div>
              <div style={{ position: 'absolute', bottom: -3, right: -3, width: '40px', height: '40px', borderBottom: '6px solid white', borderRight: '6px solid white', borderRadius: '0 0 20px 0' }}></div>
            </div>
          )}

          {/* Video Preview */}
          {cameraActive && (
            <video
              ref={videoRef}
              autoPlay
              playsInline
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          )}

          {/* Captured Image */}
          {capturedImage && !cameraActive && (
            <img
              src={capturedImage}
              alt="Captured plant"
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          )}

          {/* Placeholder when no camera/image */}
          {!cameraActive && !capturedImage && (
            <div style={{
              textAlign: 'center',
              color: '#5a6c62',
              padding: '40px'
            }}>
              <div style={{
                fontSize: '64px',
                marginBottom: '16px',
                opacity: 0.5
              }}>
                üåø
              </div>
              <p style={{
                fontSize: '18px',
                fontWeight: '600',
                color: '#0f2419',
                marginBottom: '8px'
              }}>
                Ready to Scan
              </p>
              <p style={{
                fontSize: '14px',
                opacity: 0.8
              }}>
                Start camera or upload an image of your Malunggay plant
              </p>
            </div>
          )}

          {/* Analysis Progress */}
          {analyzing && (
            <div style={{
              position: 'absolute',
              bottom: '60px',
              left: '50%',
              transform: 'translateX(-50%)',
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(10px)',
              padding: '12px 24px',
              borderRadius: '50px',
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)',
              zIndex: 20
            }}>
              <div style={{
                width: '20px',
                height: '20px',
                border: '3px solid #2d5016',
                borderTopColor: 'transparent',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite'
              }}></div>
              <span style={{
                fontSize: '14px',
                fontWeight: '600',
                color: '#2d5016'
              }}>
                {progress}% Analyzing
              </span>
            </div>
          )}

          {/* Reset button when image captured */}
          {capturedImage && !analyzing && (
            <button
              onClick={resetScan}
              style={{
                position: 'absolute',
                top: '20px',
                right: '20px',
                background: 'rgba(0, 0, 0, 0.5)',
                backdropFilter: 'blur(10px)',
                border: 'none',
                borderRadius: '50%',
                width: '40px',
                height: '40px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                cursor: 'pointer',
                zIndex: 20
              }}
            >
              <X size={24} color="white" />
            </button>
          )}
        </div>

        {/* Action Buttons */}
        <div style={{
          display: 'flex',
          gap: '12px',
          justifyContent: 'center'
        }}>
          {cameraActive ? (
            <button
              onClick={captureImage}
              disabled={analyzing}
              style={{
                background: analyzing ? '#9e9e9e' : 'linear-gradient(135deg, #2d5016 0%, #1a5f3a 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '72px',
                height: '72px',
                fontSize: '32px',
                cursor: analyzing ? 'not-allowed' : 'pointer',
                boxShadow: '0 8px 24px rgba(45, 80, 22, 0.3)',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
              onMouseOver={(e) => !analyzing && (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')}
            >
              üì∏
            </button>
          ) : (
            <>
              <button
                onClick={startCamera}
                disabled={analyzing}
                style={{
                  background: analyzing ? '#9e9e9e' : 'linear-gradient(135deg, #2d5016 0%, #1a5f3a 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '16px',
                  padding: '16px 32px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: analyzing ? 'not-allowed' : 'pointer',
                  boxShadow: '0 8px 24px rgba(45, 80, 22, 0.3)',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  flex: 1
                }}
                onMouseOver={(e) => !analyzing && (e.currentTarget.style.transform = 'translateY(-2px)')}
                onMouseOut={(e) => (e.currentTarget.style.transform = 'translateY(0)')}
              >
                <Camera size={20} />
                Start Camera
              </button>

              <input
                type="file"
                ref={fileInputRef}
                accept="image/*"
                style={{ display: 'none' }}
                onChange={handleFileUpload}
              />

              <button
                onClick={() => fileInputRef.current?.click()}
                disabled={analyzing}
                style={{
                  background: analyzing ? '#9e9e9e' : 'white',
                  color: '#2d5016',
                  border: '2px solid #2d5016',
                  borderRadius: '16px',
                  padding: '16px 32px',
                  fontSize: '16px',
                  fontWeight: '600',
                  cursor: analyzing ? 'not-allowed' : 'pointer',
                  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px',
                  flex: 1
                }}
                onMouseOver={(e) => !analyzing && (e.currentTarget.style.transform = 'translateY(-2px)')}
                onMouseOut={(e) => (e.currentTarget.style.transform = 'translateY(0)')}
              >
                <Upload size={20} />
                Upload
              </button>
            </>
          )}
        </div>

        {/* Instructions */}
        {!cameraActive && !capturedImage && (
          <p style={{
            textAlign: 'center',
            fontSize: '14px',
            color: '#5a6c62',
            margin: '0 auto',
            maxWidth: '400px'
          }}>
            Position the plant within the frame for best results. Ensure good lighting and focus on leaves for disease detection.
          </p>
        )}
      </div>

      <canvas ref={canvasRef} style={{ display: 'none' }} />

      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default ScanScreen;
