import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BottomNav from '../components/BottomNav';

const ScanScreen: React.FC = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [analyzing, setAnalyzing] = useState(false);
  const [progress, setProgress] = useState(0);

  const handleCapture = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setAnalyzing(true);
    setProgress(0);

    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setTimeout(() => {
            navigate('/analysis/demo');
          }, 500);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  return (
    <div className="phone-frame">
      <div className="screen">
        <div className="status-bar">
          <div style={{ marginLeft: '80px' }}>9:41</div>
          <div className="status-icons">üì∂ üì° üîã</div>
        </div>

        <div className="header">
          <button className="back-btn" onClick={() => navigate(-1)}>‚Üê</button>
          <div className="header-title">Recognition</div>
          <div style={{ width: '40px' }}></div>
        </div>

        <div className="recognition-area">
          {analyzing && (
            <div style={{
              position: 'absolute',
              bottom: '40px',
              background: 'white',
              padding: '12px 28px',
              borderRadius: '50px',
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              boxShadow: '0 8px 24px rgba(45, 80, 22, 0.2)'
            }}>
              <div className="spinner"></div>
              <span style={{ fontSize: '14px', fontWeight: '600' }}>
                {progress}% Analyze
              </span>
            </div>
          )}
        </div>

        <input
          type="file"
          ref={fileInputRef}
          accept="image/*"
          style={{ display: 'none' }}
          onChange={handleFileChange}
        />

        <button className="capture-btn" onClick={handleCapture}>
          üì∏ Capture Plant Part
        </button>

        <BottomNav />
      </div>
    </div>
  );
};

export default ScanScreen;
